INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48113, 6921, 'Dorzolamide (ophthalmic)', 'Liver Diseases', 'Sulfonamides may be systemically absorbed when applied to the skin, eye, or mucosal membranes.  Hepatotoxicity, including jaundice, diffuse hepatocellular necrosis, hypersensitivity hepatitis and hepatic failure, has rarely been reported in patients receiving sulfonamides.  In addition, sulfonamides are partially metabolized by the liver and may accumulate in patients with hepatic impairment.  Therapy with topical sulfonamides should be administered cautiously in patients with liver disease.', '2', '', 'DDInter', 0);
