INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48157, 2086, 'Doxazosin', 'Liver Diseases', 'Doxazosin is metabolized by the liver and subsequently excreted in the bile, primarily as metabolites, some of which are pharmacologically active.  In patients with cirrhosis (Child-Pugh Class A) given a single 2 mg dose, there was a 40% increase in exposure to doxazosin compared to patients with normal hepatic function.  The clearance was reduced by 30%, while elimination half-life was not significantly changed.  Therapy with doxazosin should be administered cautiously in patients with significantly impaired hepatic or biliary function, since drug and/or metabolite accumulation may occur.', '2', '', 'DDInter', 0);
