INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48209, 3876, 'Doxycycline', 'Liver Diseases', 'The use of tetracyclines has rarely been associated with hepatotoxicity.  Histologic fatty changes of the liver, elevated liver enzymes, and jaundice have been reported, primarily in patients treated with large doses of intravenous tetracycline hydrochloride (no longer available in the U.S.) but also in patients receiving high oral doses of these drugs.  Therapy with tetracyclines should be administered cautiously in patients with preexisting liver disease or biliary obstruction.  Reduced dosages may be appropriate, particularly with minocycline and doxycycline, since the former is metabolized by the liver and the latter undergoes enterohepatic recycling.  Liver function tests are recommended prior to and during therapy, and the concomitant use of other potentially hepatotoxic drugs should be avoided.', '2', '', 'DDInter', 0);
