INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48247, 487, 'Amantadine', 'Psychotic Disorders', 'Amantadine may precipitate or exacerbate psychotic symptoms.  Rarely, suicide attempts and suicidal ideation have also been reported, some of which occurred in patients with no prior history of psychiatric illness who were taking short courses of amantadine for influenza.  Therapy with amantadine should be administered cautiously in patients with a history of psychiatric disorders, emotional instability, or substance abuse, and particularly if they are elderly.  It may be prudent to refrain from dispensing large quantities of amantadine to these patients to prevent intentional overdose, which is potentially fatal.', '3', '', 'DDInter', 0);
