INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48553, 30670, 'Durvalumab', 'Kidney Diseases', 'Durvalumab can cause immune-mediated nephritis.  Monitor patients for abnormal renal function tests prior to and periodically during treatment.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents followed by corticosteroid taper for life-threatening increased serum creatinine.  Withhold or permanently discontinue durvalumab based on severity.  If appropriate modify the dose according to manufacturer recommendations.  Care should be taken when using durvalumab in patients with renal dysfunction.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48554, 30671, 'Durvalumab', 'Kidney Diseases', 'Durvalumab can cause immune-mediated nephritis.  Monitor patients for abnormal renal function tests prior to and periodically during treatment.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents followed by corticosteroid taper for life-threatening increased serum creatinine.  Withhold or permanently discontinue durvalumab based on severity.  If appropriate modify the dose according to manufacturer recommendations.  Care should be taken when using durvalumab in patients with renal dysfunction.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48555, 30670, 'Durvalumab', 'Thyroid Diseases', 'Durvalumab can cause immune-mediated thyroid disorders.  Monitor thyroid function prior to and periodically during treatment.  Administer hormone-replacement therapy for hypothyroidism or initiate medical management for control of hyperthyroidism.  Continue durvalumab for hypothyroidism and interrupt for hyperthyroidism based on the manufacturer recommendation.  Care should be taken when using this agent in patients with thyroid disease.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48556, 30671, 'Durvalumab', 'Thyroid Diseases', 'Durvalumab can cause immune-mediated thyroid disorders.  Monitor thyroid function prior to and periodically during treatment.  Administer hormone-replacement therapy for hypothyroidism or initiate medical management for control of hyperthyroidism.  Continue durvalumab for hypothyroidism and interrupt for hyperthyroidism based on the manufacturer recommendation.  Care should be taken when using this agent in patients with thyroid disease.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48557, 30670, 'Durvalumab', 'Pneumonia', 'Durvalumab can cause immune-mediated pneumonitis.  Monitor patients for signs and symptoms of pneumonitis.  Evaluate patients with suspected pneumonitis with radiographic imaging.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents for moderate pneumonitis or 1 to 4 mg per kg per day or equivalent corticosteroid for more severe pneumonitis, followed by a taper.  It is recommended to interrupt or permanently discontinue durvalumab based on the severity of symptoms.  Care should be exercised when using this agent in patients with suspected pneumonitis.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48558, 30671, 'Durvalumab', 'Pneumonia', 'Durvalumab can cause immune-mediated pneumonitis.  Monitor patients for signs and symptoms of pneumonitis.  Evaluate patients with suspected pneumonitis with radiographic imaging.  It is recommended to administer corticosteroids at a dose of 1 to 2 mg/kg/day prednisone equivalents for moderate pneumonitis or 1 to 4 mg per kg per day or equivalent corticosteroid for more severe pneumonitis, followed by a taper.  It is recommended to interrupt or permanently discontinue durvalumab based on the severity of symptoms.  Care should be exercised when using this agent in patients with suspected pneumonitis.', '2', '', 'DDInter', 0);
