INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48580, 18307, 'Ambrisentan', 'Water-Electrolyte Imbalance', 'Peripheral edema and fluid retention are known clinical consequences of pulmonary hypertension and also known effects of endothelin receptor antagonists.  Caution and monitoring is recommended if these agents are used in patients with underlying left ventricular dysfunction or underlying heart failure, as they have an increased risk for developing significant fluid retention that may need treatment or require discontinuation.', '2', '', 'DDInter', 0);
