INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (48692, 5950, 'Empagliflozin', 'Hypotension', 'Sodium-glucose cotransporter 2 (SGLT-2) inhibitors cause intravascular volume contraction which may lead to symptomatic hypotension after treatment initiation especially in the elderly, in patients with impaired renal function, patients with low systolic blood pressure, or those using diuretics .  Volume status should be assessed and corrected before starting treatment and patients should be monitored during therapy.', '2', '', 'DDInter', 0);
