INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49008, 0, 'Amifostine', 'Dehydration', 'Patients who are dehydrated should not receive amifostine therapy and appropriate hydration prior to administration of amifostine is necessary.  Hypotensive episodes have occurred in 62% of patients receiving amifostine.  Amifosine reduces the cumulative renal toxicity associated with cisplatin therapy.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49009, 0, 'Amifostine', 'Hypotension', 'Patients who are hypotensive or in state of dehydration, or those that are unable to discontinue antihypertensive treatment 24 hours prior to amifostine administration should not receive amifostine.  Transient hypotensive episodes have occurred in 62% of patients receiving amifostine.  Three percent of patients required discontinuation of amifostine.  Therapy with amifostine should be administered cautiously in patients adversely affected by hypotensive effects such as cardiovascular conditions of arrhythmia, angina, or CHF or cerebrovascular conditions of or history of stroke or ischemic attacks.  Clinical monitoring of blood pressure is necessary.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49010, 0, 'Amifostine', 'Hypocalcemia', 'Hypocalcemic effects during amifostine therapy are rare, but appear to result from inhibition of parathyroid hormone secretion and bone resorption and from increased urinary calcium elimination.  Therapy with amifostine should be administered cautiously in patients with or predisposed to hypocalcemia, including hypoparathyroidism.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49011, 16696, 'Entacapone', 'Hypotension', 'Dopamine agonists may impair the systemic regulation of blood pressure, with resultant orthostatic hypotension, especially during dose escalation.  Therapy with dopamine agonists should be monitored carefully in patients with Parkinson''s disease since they may have an impaired ability to respond to an orthostatic challenge, and also in patients receiving antihypertensive drugs.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49012, 24580, 'Entacapone', 'Hypotension', 'Dopamine agonists may impair the systemic regulation of blood pressure, with resultant orthostatic hypotension, especially during dose escalation.  Therapy with dopamine agonists should be monitored carefully in patients with Parkinson''s disease since they may have an impaired ability to respond to an orthostatic challenge, and also in patients receiving antihypertensive drugs.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49013, 16696, 'Entacapone', 'Psychotic Disorders', 'Ordinarily, patients with major psychotic disorder should not be treated with dopaminergic antiparkinson agents, because of the risk of exacerbating psychosis.  Hallucinations and psychotic-like behavior have been reported with dopaminergic medications.  In addition, certain medications used to treat psychosis may exacerbate the symptoms of Parkinson''s disease and may decrease the effectiveness of these drugs.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49014, 24580, 'Entacapone', 'Psychotic Disorders', 'Ordinarily, patients with major psychotic disorder should not be treated with dopaminergic antiparkinson agents, because of the risk of exacerbating psychosis.  Hallucinations and psychotic-like behavior have been reported with dopaminergic medications.  In addition, certain medications used to treat psychosis may exacerbate the symptoms of Parkinson''s disease and may decrease the effectiveness of these drugs.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49015, 16696, 'Entacapone', 'Hepatic Insufficiency', 'Entacapone should be used with caution in patients with hepatic impairment, as the AUC and Cmax of the drug approximately doubled in patients with documented liver disease compared to patients with normal liver function.', '2', '', 'DDInter', 0);
