INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49094, 0, 'Epoprostenol', 'Heart Failure', 'The use of epoprostenol is contraindicated in patients with heart failure due to reduced severe left ventricular systolic dysfunction.  A large study involving such patients was terminated after an interim analysis revealed a higher mortality in the group receiving epoprostenol plus standard therapy than in the group receiving standard therapy alone.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49095, 0, 'Epoprostenol', 'Hemorrhage', 'Epoprostenol is a potent inhibitor of platelet aggregation.  Therefore, expect an increased risk for hemorrhagic complications, particularly for patients with other risk factors for bleeding.  Caution and close monitoring is advised.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49096, 22120, 'Erlotinib', 'Stevens-Johnson Syndrome', 'Cutaneous reactions, in some cases severe, have been reported with the use of EGFR inhibitors.  Monitor patients who develop dermatologic or soft tissue toxicities while receiving these agents for the development of inflammatory or infectious sequelae.  It is recommended to withhold treatment, and appropriate measures should be instituted as appropriate or discontinue the use of these agents for dermatologic or soft tissue toxicity associated with severe or life-threatening inflammatory or infectious complications.  Advise patients to wear sunscreen and hats and limit sun exposure while receiving therapy with these agents as exposure to sunlight can exacerbate dermatologic toxicities.', '3', '', 'DDInter', 0);
