INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49101, 27905, 'Erlotinib', 'Gastrointestinal Diseases', 'The use of some EGFR inhibitors may be associated with gastrointestinal perforation and fatal cases have been reported.  Patients receiving concomitant anti-angiogenic agents, corticosteroids, NSAIDs, or taxane-based chemotherapy, or who have prior history of peptic ulceration or diverticular disease may be at increased risk of perforation.  Permanently discontinue these agents in patients who develop gastrointestinal perforation.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49102, 22120, 'Erlotinib', 'Eye Diseases', 'Some epidermal growth factor receptor (EGFR) inhibitors should be administered cautiously in patients with ocular disorders such as primary keratitis, ulcerative keratitis, or conjunctivitis.  It is recommended to interrupt or discontinue treatment with these agents if patients present with acute or worsening ocular disorders.', '3', '', 'DDInter', 0);
