INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49219, 6543, 'Ertapenem', 'Kidney Diseases', 'Carbapenems are primarily eliminated by the kidney.  Patients with renal impairment may be at greater risk for adverse effects from carbapenems, including seizures and other central nervous system disturbances, due to decreased drug clearance.  Dosage adjustments should be considered, with modifications based on degree of renal impairment and severity of infection in accordance with the individual product package labeling.  Renal function tests should be performed periodically during therapy.', '3', '', 'DDInter', 0);
