INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49223, 6543, 'Ertapenem', 'Diseases requiring hemodialysis', 'Ertapenem is partially removed by hemodialysis.  If the recommended daily dose of 500 mg is administered within 6 hours prior to hemodialysis, a supplementary dose of 150 mg is recommended following the dialysis session.  If given at least 6 hours prior to hemodialysis, no supplementary dose is needed.', '2', '', 'DDInter', 0);
