INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49225, 6543, 'Ertapenem', 'Heart Failure', 'Parenteral ertapenem contains approximately 137 mg (6.0 mEq) of sodium per each gram of ertapenem activity.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', '', 'DDInter', 0);
