INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49247, 10047, 'Ergocalciferol', 'Liver Diseases', 'Vitamin D analogs are fat soluble and oral formulations require bile for adequate intestinal absorption.  Hepatic and/or biliary dysfunction decrease the absorption of vitamin D analogs.  Metabolites of vitamin D analogs are primarily excreted in bile and feces.  Ergocalciferol, cholecalciferol, and dihydrotachysterol undergo hepatic hydroxylation during metabolic activation.  Hepatic impairment can alter the metabolic and therapeutic activity of certain vitamin D analogs.  Alternative vitamin D analogs such as calcifediol (requires renal activation) and calcitriol (active form) may be considered in patients with compromised hepatic function.', '2', '', 'DDInter', 0);
