INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49259, 16834, 'Eprosartan', 'Hypotension', 'Angiotensin II receptor (AR) antagonists can cause symptomatic hypotension in patients with an activated renin-angiotensin system, such as volume- and/or sodium-depleted patients.  Therapy with AR antagonists should be administered cautiously in such patients and in those predisposed to hypovolemic or hyponatremic states (e.g., patients on diuretic therapy, especially if high doses were used or if recently instituted; those on dietary salt restriction; renal dialysis patients).  Volume and/or sodium depletion should be corrected prior to initiating therapy with AR antagonists, and the patient should be hemodynamically stable.  Ideally, patients at risk for excessive hypotension should initiate AR antagonist therapy under close medical supervision, preferably with a lower dose, and followed closely for the first 2 weeks of treatment and whenever the dosage of AR antagonist or diuretic is increased.', '3', '', 'DDInter', 0);
