INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49279, 16834, 'Eprosartan', 'Liver Diseases', 'Eprosartan is partially metabolized by the liver, and both parent drug and metabolites are eliminated by biliary excretion (90%) as well as by the kidney (7%).  Dosage adjustments are not necessary in patients with mild renal impairment unless they are also volume-depleted, in which case therapy should be initiated under medical supervision.  Likewise, patients with mild hepatic impairment or biliary obstruction generally do not require a dosage adjustment.  However, reduced dosages may be appropriate in patients with severe renal, liver, and/or biliary disease, since the clearance may be decreased and unbound drug increased in these states.', '2', '', 'DDInter', 0);
