INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49294, 18454, 'Epirubicin', 'Liver Diseases', 'Patients with severe hepatic dysfunction should not be treated with epirubicin.  The major route of elimination of epirubicin is the hepatobiliary system.  Serum total bilirubin and AST levels should be evaluated before and during treatment with epirubicin.  Patients with elevated bilirubin or AST may experience slower clearance of drug with an increase in overall toxicity.  Lower doses are recommended in these patients.', '3', '', 'DDInter', 0);
