INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49305, 18454, 'Epirubicin', 'Neutropenia', 'Patients should not be treated with epirubicin if they have a baseline neutrophil count < 1500 cells/mm3.  A dose-dependent, reversible leukopenia and/or neutropenia is the predominant manifestation of hematologic toxicity associated with epirubicin and represents the most common acute dose-limiting toxicity of this drug.  Myelosuppression requires careful monitoring.  Total and differential WBC, red blood cell (RBC), and platelet counts should be assessed before and during each cycle of therapy with epirubicin.', '3', '', 'DDInter', 0);
