INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49601, 33743, 'Escitalopram', 'Liver Diseases', 'Selective serotonin reuptake inhibitors (SSRIs) are primarily metabolized by the liver.  The plasma concentrations of SSRIs and their metabolites may be increased and the half-lives prolonged in patients with impaired hepatic function.  Dosage adjustments may be necessary in accordance with the individual product package labeling.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49602, 1078, 'Escitalopram', 'Mania', 'Selective serotonin reuptake inhibitors (SSRIs), like other antidepressants, may occasionally cause or activate mania or hypomania.  The reported incidence ranged from 0.1% to 2% in premarketing testing of several SSRIs.  Patients with bipolar disorder are generally more likely to experience mania from antidepressants.  Therapy with SSRIs should be administered cautiously in patients with a history of mania or bipolar disorder.  Prior to initiating treatment, it is recommended to adequately screen patients for bipolar disorder, including a family history of suicide, bipolar disorder, and depression.', '2', '', 'DDInter', 0);
