INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49807, 4310, 'Erythropoietin', 'Seizures', 'The use of erythropoiesis stimulating agents increases the risk of seizures in patients with chronic renal failure.  Seizure development may be related to the rate of rise in hematocrit, which is also associated with blood pressure elevations.  It is recommended to monitor patients closely for the development of premonitory neurologic symptoms during the first several months after the administration of these agents.  Patients with epilepsy or predisposed to seizures should be monitored closely for blood pressure changes and neurologic symptoms during therapy with these agents.', '3', '', 'DDInter', 0);
