INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49879, 4310, 'Erythropoietin', 'Porphyrias', 'The use of epoetin alfa has rarely been associated with exacerbation of porphyria in patients with chronic renal failure.  In healthy volunteers, however, epoetin alfa has not been shown to cause increased urinary excretion of porphyrin metabolites.  Nevertheless, therapy with epoetin alfa should be administered cautiously in patients with porphyria.', '2', '', 'DDInter', 0);
