INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (49990, 1561, 'Esomeprazole', 'Liver Diseases', 'Esomeprazole is primarily metabolized by the liver.  In patients with mild to moderate hepatic impairment (Child Pugh Class A and B), little drug accumulation has been observed following once-daily, multiple-dose administration.  No dosage adjustments are necessary in these patients.  In severe hepatic impairment (Child Pugh Class C), however, substantial increases in plasma drug concentrations have been demonstrated.  The manufacturer recommends a maximum dosage of 20 mg once daily in patients with severe liver disease.', '2', '', 'DDInter', 0);
