INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50293, 18549, 'Eslicarbazepine', 'Thyroid Diseases', 'Dose-dependant decreases in thyroid function tests (serum T3 and T4) have been observed in patients taking eslicarbazepine.  These changes were not associated with other abnormal thyroid function tests suggesting hypothyroidism.  Abnormal thyroid function should be evaluated and clinicians should be cognizant of these effects when prescribing or administering eslicarbazepine therapy to patients with thyroid disorders.', '2', '', 'DDInter', 0);
