INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50329, 4438, 'Estradiol (topical)', 'Breast Neoplasms', 'The use of estrogens is generally contraindicated in patients with known or suspected estrogen-dependent neoplasia such as breast and endometrial cancer, since it may stimulate tumor proliferation.  High dosages of estrogens may be used for the palliative treatment of inoperable, metastatic breast cancer, but only in appropriately selected men and postmenopausal women.', '3', '', 'DDInter', 0);
