INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50347, 4438, 'Estradiol (topical)', 'Liver Neoplasms', 'The use of oral contraceptives is contraindicated in patients with liver tumors.  An increased risk of benign hepatic adenomas and hepatocellular carcinomas has been associated with long-term, oral estrogen- progestin contraceptive use of at least 4 years and 8 years, respectively.  Although these tumors are rare and have not been reported with other types of estrogen or progestogen therapies, any preparation containing estrogens and/or progestogens should probably be avoided in patients with existing tumors of the liver.  Hepatic hemangiomas and nodular hyperplasia of the liver have been reported with isolated estrogen therapy.', '3', '', 'DDInter', 0);
