INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50377, 4438, 'Estradiol (topical)', 'Liver Diseases', 'Estrogens are primarily metabolized by the liver.  Patients with impaired hepatic function may be at increased risk for adverse effects associated with estrogen administration due to decreased drug clearance.  Therapy with estrogens should be administered cautiously in patients with liver disease.  In addition, clinicians should be aware that estrogen therapy may affect liver function tests.  Increased sulfobromophthalein retention has been reported with the use of estrogen-containing oral contraceptives and may be expected with larger doses of estrogens.', '2', '', 'DDInter', 0);
