INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50383, 4438, 'Estradiol (topical)', 'Melanosis', 'The use of exogenous estrogens may occasionally cause chloasma, especially in women with a history of chloasma gravidarum.  Women with a tendency to chloasma should avoid exposure to the sun or ultraviolet radiation while taking combination oral contraceptives.', '2', '', 'DDInter', 0);
