INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50497, 4438, 'Estradiol', 'Glucose Intolerance', 'Impaired glucose tolerance has been observed in some patients administered oral contraceptives and appears to be related primarily to the estrogen dose.  However, progestogens can increase insulin secretion and produce insulin resistance to varying degrees, depending on the agent.  Patients with diabetes mellitus should be monitored more closely during therapy with estrogens and/or progestogens, and adjustments made accordingly in their antidiabetic regimen.', '2', '', 'DDInter', 0);
