INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50545, 5485, 'Estrone sulfate', 'Gallbladder Diseases', 'A two- to four-fold increase in risk of gallbladder disease has been noted in women receiving postmenopausal estrogen therapy.  The risk for gallbladder disease may be less for premenopausal women using oral contraceptives containing low-dose estrogens and/or progestins.  Therapy with estrogens should be administered cautiously in patients with preexisting gallbladder disease.', '2', '', 'DDInter', 0);
