INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50548, 5485, 'Estrone sulfate', 'Hypercalcemia', 'Estrogens influence the metabolism of calcium and phosphorus.  Intestinal absorption and retention of calcium are increased, which may occasionally result in hypercalcemia.  Therapy with estrogens should be administered cautiously in patients with preexisting hypercalcemia, renal dysfunction, or metabolic bone diseases that are associated with hypercalcemia.', '2', '', 'DDInter', 0);
