INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50551, 5485, 'Estrone sulfate', 'Hyperlipidemias', 'Although estrogens have generally favorable effects on plasma lipids, including increases in HDL and decreases in total cholesterol and LDL, they have also been associated with significant elevations in triglyceride levels, particularly when high dosages are used.  Severe hyperlipidemia is known to sometimes cause pancreatitis.  Patients with preexisting hyperlipidemia may require closer monitoring during estrogen therapy, and adjustments made accordingly in their lipid-lowering regimen.', '2', '', 'DDInter', 0);
