INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50560, 5485, 'Estrone sulfate', 'Depressive Disorder', 'The use of oral contraceptives has been associated with an increased incidence of depression.  It is uncertain whether this effect is related to the estrogenic or the progestogenic component of the contraceptive, although excess progesterone activity is associated with depression.  Patients with a history of depression receiving estrogen and/or progestogen therapy should be followed closely.  The manufacturer of medroxyprogesterone recommends monitoring patients who have a history of depression and to not re- administer medroxyprogesterone if depression recurs.', '2', '', 'DDInter', 0);
