INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50569, 5485, 'Estrone sulfate', 'Eye Diseases', 'Estrogens and progestogens may cause retinal thrombosis.  Oral contraceptives should be discontinued if there is unexplained partial or complete loss of vision; onset of proptosis or diplopia; papilledema; or retinal vascular lesions.  Therapy with these agents should be administered cautiously in patients who have preexisting ocular problems and appropriate diagnostic and therapeutic measures should be instituted.  Contact lens wearers who develop visual changes or changes in lens tolerance should be assessed by an ophthalmologist.', '2', '', 'DDInter', 0);
