INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50648, 29189, 'Abemaciclib', 'Diarrhea', 'Diarrhea, sometimes associated with dehydration and infection, has occurred in patients receiving abemaciclib.  Diarrhea incidence was greatest during the first month of abemaciclib dosing.  Care should be exercised when treating patients with symptoms of diarrhea.  Treatment discontinuation may be required for Grade 3 or 4 diarrhea, or diarrhea that requires hospitalization.  Resume treatment at the next lower dose once toxicity resolves to <= Grade 1 diarrhea.', '2', '', 'DDInter', 0);
