INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50652, 29189, 'Abemaciclib', 'Lung Diseases, Interstitial', 'Patients treated with abemaciclib have reported severe, life-threatening, or fatal interstitial lung disease (ILD) and/or pneumonitis.  It is recommended to monitor patients for pulmonary symptoms indicative of ILD/pneumonitis such as, hypoxia, cough, dyspnea, or interstitial infiltrates on radiologic exams.  Dose interruption or dose reduction is recommended for patients who develop persistent or recurrent Grade 2 ILD/pneumonitis.  Permanently discontinue therapy in all patients with Grade 3 or 4 ILD or pneumonitis.', '2', '', 'DDInter', 0);
