INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50700, 4446, 'Etodolac', 'Exanthema', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) can cause serious skin adverse reactions (e.g., Stevens-Johnson syndrome, toxic epidermal necrolysis, and exfoliative dermatitis), which can be fatal.  These serious events may occur without warning.  Patients should be advised to discontinue the NSAID and seek medical attention promptly at the first sign of skin rash or any other sign of hypersensitivity.  NSAIDs are contraindicated in patients with previous serious skin reactions to these drugs.', '3', '', 'DDInter', 0);
