INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50800, 2361, 'Everolimus', 'Hyperglycemia', 'Elevations in serum blood glucose levels have been reported in patients taking inhibitors of mTOR (mammalian target of rapamycin).  Monitoring of fasting serum glucose levels is recommended prior to the start of therapy and periodically thereafter.  Clinicians should achieve control of glucose levels before initiating therapy with these agents.', '2', '', 'DDInter', 0);
