INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (50821, 2361, 'Everolimus', 'Wounds and Injuries', 'Inhibition of mTOR activity results in delays of wound healing and increases the occurrence of wound-related complications, which might require surgical intervention.  Patients with central nervous system tumors (primary CNS tumor or metastases) and/or receiving anticoagulation therapy may be at an increased risk of developing intracerebral bleeding (including fatal outcomes).  Caution is recommended when using these agents, particularly in the perioperative period.', '2', '', 'DDInter', 0);
