INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51170, 17996, 'Famciclovir', 'Liver Diseases', 'Famciclovir is converted by the liver to the active antiviral metabolite, penciclovir.  The bioavailability of penciclovir following a dose of famciclovir was not affected in patients with well-compensated chronic liver disease.  However, there are no data on the pharmacokinetic disposition of famciclovir in patients with severe uncompensated liver disease.  Therapy with famciclovir should be administered cautiously in these patients, since the clinical significance of possible famciclovir accumulation is unknown.  Theoretically, famciclovir may also not be effective in these patients.', '2', '', 'DDInter', 0);
