INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51201, 30829, 'Fenofibrate', 'Cholelithiasis', 'The use of fibric acid derivatives is contraindicated in patients with gallbladder disease.  A significantly increased incidence of cholelithiasis has been observed in patients treated with the fibric acid derivative, clofibrate, presumably because of increased cholesterol excretion into the bile.  Based on two separate studies (the WHO study and the Coronary Drug Project study), clofibrate use was associated with twice the risk of developing cholelithiasis and cholecystitis requiring surgery.  Due to their structural and pharmacologic similarities, use of other fibric acid derivatives may be expected to carry the same risk.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51202, 4796, 'Fenofibrate', 'Hypolipoproteinemias', 'There have been reports of severe decreases in HDL cholesterol (HDL-C) levels (as low as 2 mg/dL) occurring in diabetic and non-diabetic patients initiated on fibric acid derivatives therapy.  The decrease has been reported to occur within 2 weeks to years after initiation of therapy.  It is recommended that HDL-C levels be checked within the first few months after initiation therapy and if a severely depressed HDL-C level is detected, therapy with these agents should be withdrawn.  Monitor HDL-C level until it has returned to baseline, and therapy with these agents should not be re-initiated in these patients.', '3', '', 'DDInter', 0);
