INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51230, 4796, 'Fenofibrate', 'Kidney Diseases', 'The use of fibric acid derivatives is contraindicated in patients with significantly impaired renal function.  The rate of clearance of fenofibric acid has been shown to decrease substantially when CrCl is below 50 mL/min, with drug accumulation during chronic dosing.  Increased adverse effects, including rhabdomyolysis (with or without secondary renal failure) and severe hyperkalemia, have been associated with the use of fibric acid derivatives in patients with renal insufficiency.  Therapy with these agents should be administered cautiously in patients with mild or moderate renal impairment.  Close clinical monitoring is recommended during therapy.', '3', '', 'DDInter', 0);
