INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51258, 4796, 'Fenofibrate', 'Hematologic Diseases', 'Mild to moderate hemoglobin, hematocrit and white blood cell decreases have been observed in patients following initiation of fibric acid derivatives therapy.  However, these levels stabilize during long-term administration.  Thrombocytopenia and agranulocytosis have been reported in individuals treated with these agents.  Caution is recommended when treating patients predisposed to hematologic changes.  Periodic monitoring of red and white blood cell counts is recommended during the first 12 months of therapy.', '2', '', 'DDInter', 0);
