INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51278, 29046, 'Fenofibric acid', 'Cholelithiasis', 'The use of fibric acid derivatives is contraindicated in patients with gallbladder disease.  A significantly increased incidence of cholelithiasis has been observed in patients treated with the fibric acid derivative, clofibrate, presumably because of increased cholesterol excretion into the bile.  Based on two separate studies (the WHO study and the Coronary Drug Project study), clofibrate use was associated with twice the risk of developing cholelithiasis and cholecystitis requiring surgery.  Due to their structural and pharmacologic similarities, use of other fibric acid derivatives may be expected to carry the same risk.', '3', '', 'DDInter', 0);
