INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51290, 0, 'Fenoldopam', 'Heart Failure', 'Fenoldopam causes a dose-related fall in blood pressure that may worsen congestive heart failure.  Therapy with fenoldopam should be administered cautiously in patients with heart failure.  Monitoring blood pressure at regular intervals is recommended.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51291, 0, 'Fenoldopam', 'Glaucoma', 'Fenoldopam causes a dose-dependent increase in intraocular pressure.  Therapy with fenoldopam should be administered cautiously in patients with glaucoma or intraocular hypertension.  Intraocular pressure may return to baseline in 2 hours following discontinuation of fenoldopam.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51292, 0, 'Fenoldopam', 'Hypokalemia', 'The use of fenoldopam is associated with hypokalemia (less than 3 mEq/L after 6 hours of infusion).  Therapy with fenoldopam should be administered cautiously in patients with preexisting hypokalemia.  Monitoring serum electrolytes and potassium replacement as required is recommended.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51293, 0, 'Fenoldopam', 'Hypotension', 'Fenoldopam may cause symptomatic hypotension that may exacerbate cerebral infarction or hemorrhage.  Monitoring blood pressure closely during administration is recommended.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51294, 0, 'Fenoldopam', 'Tachycardia', 'The use of fenoldopam is associated with producing dose-related compensatory tachycardia, particularly with infusion rates greater than 0.1 mcg/kg/min in adults and >0.8 mcg/kg/min in pediatric patients.  Tachycardia may diminish over time, but remains substantial at higher doses.  Care should be exercised when using this agent in patients at risk.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51295, 8544, 'Fenoprofen', 'Asthma', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) are contraindicated in patients with history of asthma, urticaria, or other allergic-type reactions after taking aspirin or other NSAIDs; severe, sometimes fatal, anaphylactic reactions to NSAIDs have been reported in such patients.  A subpopulation of patients with asthma may have aspirin-sensitive asthma which may include chronic rhinosinusitis complicated by nasal polyps, severe potentially fatal bronchospasm, and/or intolerance to aspirin and other NSAIDs.  Since cross-reactivity between aspirin and other NSAIDs has been reported in such aspirin-sensitive patients, therapy with any NSAID should be avoided in patients with this form of aspirin sensitivity.  NSAIDs should be used with caution in patients with preexisting asthma (without known aspirin sensitivity), and these patients should be monitored for changes in the signs and symptoms of asthma.', '3', '', 'DDInter', 0);
