INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51331, 4802, 'Fentanyl', 'Gastrointestinal Diseases', 'Narcotic (opioid) analgesic agents increase smooth muscle tone in the gastrointestinal tract and decrease peristalsis, which can lead to elevated intraluminal pressure, spasm, and constipation following prolonged use.  In patients with severe or acute inflammatory bowel disease, the decrease in colonic motility may induce toxic megacolon.  Therapy with opioids should be administered cautiously in patients with gastrointestinal obstruction, constipation, inflammatory bowel disease, or recent gastrointestinal tract surgery.  Gastrointestinal effects appear to be the most pronounced with morphine.', '3', '', 'DDInter', 0);
