INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51379, 4802, 'Fentanyl', 'Alcoholic Intoxication', 'The use of opiate agonists is contraindicated in patients with acute alcohol intoxication exhibiting depressed vital signs.  The central nervous system depressant effects of opiate agonists may be additive with those of alcohol.  Severe respiratory depression and death may occur.  Therapy with opiate agonists should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '', 'DDInter', 0);
