INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51499, 4802, 'Fentanyl', 'Kidney Diseases', 'Although narcotic (opioid) analgesic agents are generally metabolized by the liver, renal impairment can alter the elimination of these agents and their metabolites (some of which are pharmacologically active), resulting in drug accumulation and increased risk of toxicity.  Therapy with opioids should be administered cautiously and initiated at reduced dosages in patients with significantly impaired renal function.  Subsequent doses should be titrated based on individual response rather than a fixed dosing schedule.', '2', '', 'DDInter', 0);
