INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51685, 24890, 'Aminolevulinic acid (topical)', 'Porphyrias', 'Photoactivation following topical application of aminolevulinic acid topical gel occurs when aminolevulinic acid (prodrug) is metabolized to protoporphyrin IX, a photoactive compound which accumulates in the skin.  Therapy with aminolevulinic acid topical gel increases photosensitivity and it may cause uncontrolled phototoxic effects.  Its use in contraindicated in patients with porphyria or photodermatoses.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (51686, 24890, 'Aminolevulinic acid (topical)', 'Hemorrhage', 'The use of aminolevulinic acid topical gel increases the risk of bleeding in patients with coagulation disorders.  Care should be exercised during lesion preparation to avoid bleeding.', '2', '', 'DDInter', 0);
