INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52006, 14155, 'Flumazenil', 'Seizures', 'Flumazenil has been associated with seizure activity, particularly in patients receiving repeated doses of benzodiazepines for long term sedation, acute overdose involving concurrent cyclic antidepressants, or head injury where it may alter cerebral blood flow in patients receiving benzodiazepines.  Therapy with flumazenil should be administered cautiously in patients with a history of or predisposition to seizures.', '3', '', 'DDInter', 0);
