INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (52010, 14155, 'Flumazenil', 'Liver Diseases', 'Flumazenil is a highly extracted drug, dependent on blood flow, and primarily metabolized by the liver.  Hepatic clearance is decreased 40% to 60 % in patients with moderate hepatic dysfunction and 75% in patients with severe hepatic dysfunction.  Care should be exercised as the metabolic and therapeutic activity of flumazenil may be altered in patients with compromised hepatic function or conditions that decrease systemic blood flow.  Following the initial dose, subsequent doses or the frequency of administration should be reduced.', '2', '', 'DDInter', 0);
